config.files:
{
    "iw4m/Configuration/IW4MAdminSettings.json": {
        "parser": "json",
        "find": {
            "Servers.*.IPAddress": "{{server.build.default.ip}}",
            "Servers.*.Password": "{{server.build.env.RCON_PASSWORD}}",
            "Servers.*.Port": "{{server.build.default.port}}",
            "Servers.*.ManualLogPath": "/home/container/boiii/{{server.build.env.LOG_FILE}}.log"
        }
    },
    "zone/server.cfg": {
        "parser": "file",
        "find": {
            "set rcon_password": "set rcon_password \"{{server.build.env.RCON_PASSWORD}}\""
        }
    },
    "zone/server_cp.cfg": {
        "parser": "file",
        "find": {
            "set rcon_password": "set rcon_password \"{{server.build.env.RCON_PASSWORD}}\""
        }
    },
    "zone/server_zm.cfg": {
        "parser": "file",
        "find": {
            "set rcon_password": "set rcon_password \"{{server.build.env.RCON_PASSWORD}}\""
        }
    }
}
